import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import toast from "react-hot-toast";
import axiosInstance from "../../Helper/axiosInstance.js";

const storedData = localStorage.getItem('data');

let parsedData = {};
if (storedData && storedData !== "undefined") {
    try {
        parsedData = JSON.parse(storedData);
    } catch (error) {
        localStorage.removeItem('data');
    }
}

const initialState = {
    isLoggedIn: localStorage.getItem('isLoggedIn') === 'true' || false,
    role: localStorage.getItem('role') || "",
    data: parsedData
};

export const login = createAsyncThunk("/auth/login", async (data, { rejectWithValue }) => {
    try {
        const response = await axiosInstance.post("user/login", data);
        if (response.data && response.data.user) {
            toast.success(response.data.message || "Login successful! Done.");
            return response.data;
        } else {
            const errorMessage = response.data.message || "Login failed: No user data received.";
            toast.error(errorMessage);
            return rejectWithValue(errorMessage);
        }
    } catch (error) {
        const errorMessage = error?.response?.data?.message || "Failed to log in";
        toast.error(errorMessage);
        return rejectWithValue(errorMessage);
    }
});

export const logout = createAsyncThunk("/auth/logout", async (_, { rejectWithValue }) => {
    try {
        const response = await axiosInstance.post("user/logout");
        toast.success(response.data.message || "Logout successful!");
        return response.data;
    } catch (error) {
        const errorMessage = error?.response?.data?.message || "Failed to log out";
        toast.error(errorMessage);
        return rejectWithValue(errorMessage);
    }
});

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(login.fulfilled, (state, action) => {
                localStorage.setItem("data", JSON.stringify(action?.payload?.user));
                localStorage.setItem("isLoggedIn", true);
                localStorage.setItem("role", action?.payload?.user?.role);
                state.isLoggedIn = true;
                state.data = action?.payload?.user;
                state.role = action?.payload?.user?.role;
            })
            .addCase(logout.fulfilled, (state) => {
                localStorage.clear();
                state.data = {};
                state.isLoggedIn = false;
                state.role = "";
            });
    }
});

export default authSlice.reducer;

import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axiosInstance from "../../Helper/axiosInstance.js";
import toast from "react-hot-toast";

const initialState = {
    key: "",
    subscription_id: "",
    isPaymentVerified: false,
    allPayments: {},
    finalMonth: {},
    monthlySalesRecord: [],
};

export const getRazorPayId = createAsyncThunk("/razorpay/getId", async () => {
    try {
        const response = await axiosInstance.get("/payment/razorpay-key");
        return response.data;
    } catch (error) {
        toast.error("Failed to load data");
    }
});

export const purchaseCourseBundle = createAsyncThunk("/purchaseCourse", async () => {
    try {
        const response = await axiosInstance.post("/payment/subscribe");
        return response.data;
    } catch (error) {
        toast.error(error?.response?.data?.message || "Subscription failed");
    }
});

export const verifyUserPayment = createAsyncThunk("/payment/verify", async (data) => {
    try {
        const response = await axiosInstance.post("/payment/verify", {
            razorpay_payment_id: data.razorpay_payment_id,
            razorpay_subscription_id: data.razorpay_subscription_id,
            razorpay_signature: data.razorpay_signature,
        });
        return response.data;
    } catch (error) {
        toast.error(error?.response?.data?.message || "Payment verification failed");
    }
});

export const getPaymentRecord = createAsyncThunk("/payments/record", async () => {
    try {
        const response = axiosInstance.get("/payment?count=100");
        toast.promise(response, {
            loading: "Getting the payment records...",
            success: (data) => {
                return data?.data?.message || "Payment records loaded successfully";
            },
            error: "Failed to get payment records",
        });
        return (await response).data;
    } catch (_error) {
        toast.error("Operation failed");
    }
});

export const cancelCourseBundle = createAsyncThunk("/payments/cancel", async () => {
    try {
        const response = axiosInstance.post("/payment/unsubscribe");
        toast.promise(response, {
            loading: "Unsubscribing the bundle...",
            success: (data) => {
                return data?.data?.message || "Unsubscribed successfully";
            },
            error: "Failed to unsubscribe",
        });
        return (await response).data;
    } catch (_error) {
        toast.error("Failed to unsubscribe");
    }
});

const razorpaySlice = createSlice({
    name: "razorpay",
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(getRazorPayId.fulfilled, (state, action) => {
                state.key = action?.payload?.key;
            })
            .addCase(purchaseCourseBundle.fulfilled, (state, action) => {
                state.subscription_id = action?.payload?.subscription_id;
            })
            .addCase(verifyUserPayment.fulfilled, (state, action) => {
                toast.success(action?.payload?.message || "Payment verified successfully");
                state.isPaymentVerified = action?.payload?.success;
            })
            .addCase(verifyUserPayment.rejected, (state, action) => {
                toast.error(action?.payload?.message || "Payment verification failed");
                state.isPaymentVerified = action?.payload?.success;
            })
            .addCase(getPaymentRecord.fulfilled, (state, action) => {
                state.allPayments = action?.payload?.allPayments;
                state.finalMonth = action?.payload?.finalMonth;
                state.monthlySalesRecord = action?.payload?.monthlySalesRecord;
            });
    },
});

export default razorpaySlice.reducer;
